true" ID="7kkb0n92bsqn4josbiivhtqk35" MODIFIED="1744791702731" TEXT="Cookies"><node CREATED="1744791702731" FOLDER="true" ID="7pdf8g21drr2ngbn4dgrbk4ora" MODIFIED="1744791702731" TEXT="客户端存储"/></node><node CREATED="1744791702731" FOLDER="true" ID="4usnrh53lkac19jsj2ptaocjfu" MODIFIED="1744791702731" TEXT="Session"><node CREATED="1744791702731" FOLDER="true" ID="59egqomtdntn9fqhj246t5fvvp" MODIFIED="1744791702731" TEXT="服务器端存储"/><node CREATED="1744791702731" FOLDER="true" ID="5end6m9qm9li2edi3ik20bq25s" MODIFIED="1744791702731" TEXT="服务器端会话机制 跟踪用户会话状态"><node CREATED="1744791702731" FOLDER="true" ID="51kbolc2us35umvafqd7ic1pmb" MODIFIED="1744791702731" TEXT="当用户访问服务器时，服务器会为每个用户创建一个唯一的 Session ID，并将其通过 Cookies 或 URL 参数发送给客户端。客户端在后续请求中携带这个 Session ID，服务器根据这个 ID 来查找对应的 Session 数据，从而识别用户并维护用户的会话状态"/></node></node><node CREATED="1744791702731" FOLDER="true" ID="5dof4re7cmsv50chn6gn4qvv71" MODIFIED="1744791702731" TEXT="Token"><node CREATED="1744791702731" FOLDER="true" ID="23a7t4662784mqejhu8ve1hh9t" MODIFIED="1744791702731" TEXT="无状态"/><node CREATED="1744791702731" FOLDER="true" ID="3lp6r7uq5n7cqkr6pt5frubksu" MODIFIED="1744791702731" TEXT="服务器生成的身份验证凭证"><node CREATED="1744791702731" FOLDER="true" ID="43186qpsn376pa8p7abv53htcq" MODIFIED="1744791702731" TEXT="客户端请求携带，服务器验证"/></node><node CREATED="1744791702731" FOLDER="true" ID="5mjhqcml75md305l1tveseqini" MODIFIED="1744791702731" TEXT="跨域支持"/></node></node></node><node CREATED="1744791702731" FOLDER="true" ID="3c8kk5gsc7tpq9g7h0fnveg14k" MODIFIED="1744791702731" POSITION="right" TEXT="Linux"><node CREATED="1744791702731" FOLDER="true" ID="3k6sej2b2l2rvu97per0d94rih" MODIFIED="1744791702731" TEXT="CLI"><node CREATED="1744791702731" FOLDER="true" ID="4rej66msgs35q6l9t409l23jjj" MODIFIED="1744791702731" TEXT="pwd"><node CREATED="1744791702731" FOLDER="true" ID="466gs8uvheon7a5r8m049a8e12" MODIFIED="1744791702731" TEXT="ls [-a/l]"><node CREATED="1744791702731" FOLDER="true" ID="62cjgbglv0cl3j7p1smchtkhrp" MODIFIED="174479170